import React, { useState } from 'react';
import { Card, CardHeader, CardBody, CardTitle, Row, Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import './UploadManager.css'; // Create a CSS file for styling if necessary

function UploadManager() {
  const [questionFormOpen, setQuestionFormOpen] = useState(false);
  const [notesFormOpen, setNotesFormOpen] = useState(false);
  const [selectedBatch, setSelectedBatch] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [selectedType, setSelectedType] = useState('');
  const [file, setFile] = useState(null);

  const handleQuestionFormOpen = () => {
    setQuestionFormOpen(true);
    setNotesFormOpen(false);
  };

  const handleNotesFormOpen = () => {
    setNotesFormOpen(true);
    setQuestionFormOpen(false);
  };

  const handleBatchChange = (event) => {
    setSelectedBatch(event.target.value);
  };

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  const handleCourseChange = (event) => {
    setSelectedCourse(event.target.value);
  };

  const handleTypeChange = (event) => {
    setSelectedType(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = () => {
    // Handle file upload logic here
    console.log('Uploading file:', file);
    // Reset state after upload
    setFile(null);
    setSelectedBatch('');
    setSelectedSubject('');
    setSelectedCourse('');
    setSelectedType('');
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h5">Upload Manager</CardTitle>
              <p className="card-category">Add and manage uploads</p>
            </CardHeader>
            <CardBody>
              <Button color="primary" onClick={handleQuestionFormOpen}>Add Question</Button>
              <Button color="primary" onClick={handleNotesFormOpen}>Add Notes</Button>

              {questionFormOpen && (
                <div>
                  <h2>Add Question</h2>
                  <Form>
                    <FormGroup>
                      <Label for="batchSelect">Select Batch:</Label>
                      <Input type="select" id="batchSelect" value={selectedBatch} onChange={handleBatchChange}>
                        {/* Populate with batches */}
                        <option value="">Select Batch</option>
                        <option value="batch1">Batch 1</option>
                        <option value="batch2">Batch 2</option>
                      </Input>
                    </FormGroup>
                    <FormGroup>
                      <Label for="subjectInput">Subject:</Label>
                      <Input type="text" id="subjectInput" value={selectedSubject} onChange={handleSubjectChange} />
                    </FormGroup>
                    {/* Add more question-related fields here */}
                  </Form>
                </div>
              )}

              {notesFormOpen && (
                <div>
                  <h2>Add Notes</h2>
                  <Form>
                    <FormGroup>
                      <Label for="courseSelect">Select Course:</Label>
                      <Input type="select" id="courseSelect" value={selectedCourse} onChange={handleCourseChange}>
                        {/* Populate with courses */}
                        <option value="">Select Course</option>
                        <option value="course1">Course 1</option>
                        <option value="course2">Course 2</option>
                      </Input>
                    </FormGroup>
                    <FormGroup>
                      <Label for="typeSelect">Select Type:</Label>
                      <Input type="select" id="typeSelect" value={selectedType} onChange={handleTypeChange}>
                        <option value="video">Video</option>
                        <option value="document">Document</option>
                      </Input>
                    </FormGroup>
                    {selectedType === 'video' ? (
                      <FormGroup>
                        <Input type="file" onChange={handleFileChange} />
                      </FormGroup>
                    ) : (
                      <FormGroup>
                        {/* Add options for document upload */}
                      </FormGroup>
                    )}
                    <Button color="success" onClick={handleUpload}>Upload</Button>
                  </Form>
                </div>
              )}
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default UploadManager;
