import React from 'react';
import Dashboard from 'views/Dashboard';
import Notifications from 'views/Notifications';
import Icons from 'views/Icons';
import Typography from 'views/Typography';
import TableList from 'views/TableList';
import UserPage from 'views/UserPage';
import ManageCoursePage from 'views/ManageCoursePage';
import BatchManager from 'views/BatchManager';
import UpgradeToPro from 'views/UpgradeToPro';
import Courses from 'views/Courses';
import Liveclass from 'views/Liveclass';
import QandA from 'views/QandA';

const getRoutes = (userCategory) => {
  let routes = [];

  if (userCategory === 'admin') {
    routes = [
      {
        path: '/dashboard',
        name: 'Dashboard',
        icon: 'nc-icon nc-chart-bar-32',
        component: <Dashboard />,
        layout: '/admin',
      },
      {
        path: '/notifications',
        name: 'Manage Faculties',
        icon: 'nc-icon nc-hat-3',
        component: <Notifications />,
        layout: '/admin',
      },
      {
        path: '/icons',
        name: 'Manage Student',
        icon: 'nc-icon nc-single-02',
        component: <Icons />,
        layout: '/admin',
      },
      {
        path: '/user-page',
        name: 'Profile',
        icon: 'nc-icon nc-bookmark-2',
        component: <UserPage />,
        layout: '/admin',
      },
      {
        path: '/manage-course-page',
        name: 'Manage Course',
        icon: 'nc-icon nc-book-bookmark',
        component: <ManageCoursePage />,
        layout: '/admin',
      },
      {
        path: '/batch-manager',
        name: 'Batch Manage',
        icon: 'nc-icon nc-watch-time',
        component: <BatchManager />,
        layout: '/admin',
      },
      {
        path: '/tables',
        name: 'Manage Schedule',
        icon: 'nc-icon nc-calendar-60',
        component: <TableList />,
        layout: '/admin',
      },
      {
        path: '/typography',
        name: 'Manage Exam',
        icon: 'nc-icon nc-ruler-pencil',
        component: <Typography />,
        layout: '/admin',
      },
      {
        path: '/upgrade',
        name: 'Manage Certification',
        icon: 'nc-icon nc-trophy',
        component: <UpgradeToPro />,
        layout: '/admin',
      },
    ];
  } else if (userCategory === 'student') {
    routes = [
      {
        path: '/dashboard',
        name: 'Dashboard',
        icon: 'nc-icon nc-chart-bar-32',
        component: <Dashboard />,
        layout: '/admin',
      },
      {
        path: '/liveclass',
        name: 'My Classes',
        icon: 'nc-icon nc-time-alarm',
        component: <Liveclass />,
        layout: '/admin',
      },
      {
        path: '/courses',
        name: 'My Courses',
        icon: 'nc-icon nc-single-copy-04',
        component: <Courses />,
        layout: '/admin',
      },
      {
        path: '/qanda',
        name: 'Question and Answer',
        icon: 'nc-icon nc-bullet-list-67',
        component: <QandA />,
        layout: '/admin',
      },
      {
        path: '/upgrade',
        name: 'Upgrade',
        icon: 'nc-icon nc-trophy',
        component: <UpgradeToPro />,
        layout: '/admin',
      },
    ];
  }

  return routes;
};

export default getRoutes;
