import React, { useState, useEffect } from 'react';
import Dashboard from "views/Dashboard.js";
import Notifications from "views/Notifications.js";
import Icons from "views/Icons.js";
import Typography from "views/Typography.js";
import TableList from "views/Tables.js";
import UserPage from "views/User.js";
import ManageCoursePage from "views/ManageCoursePage.js";
import BatchManager from "views/BatchManager.js";
import Courses from "views/Courses";
import Liveclass from "views/Liveclass";
import QandA from "views/QandA";
import UpgradeToPro from "views/Upgrade.js";

function routes() {
  const [userCategory, setUserCategory] = useState(null);

  useEffect(() => {
    // Fetch user's category from the database
    async function fetchUserCategory() {
      try {
        const response = await fetch('/api/getUserCategory');
        const data = await response.json();
        setUserCategory(data.category);
      } catch (error) {
        console.error('Error fetching user category:', error);
      }
    }

    fetchUserCategory();
  }, []);

  // Define routes based on user's category
  const routes = [
    {
      path: "/dashboard",
      name: "Dashboard",
      icon: "nc-icon nc-chart-bar-32",
      component: <Dashboard />,
      layout: "/admin",
    },
    {
      path: "/Icons",
      name: "Manage Student",
      icon: "nc-icon nc-single-02",
      component: <Icons />,
      layout: "/admin",
    },
    {
      path: "/notifications",
      name: "Manage Faculties",
      icon: "nc-icon nc-hat-3",
      component: <Notifications />,
      layout: "/admin",
    },
    {
      path: "/user-page",
      name: "Profile",
      icon: "nc-icon nc-bookmark-2",
      component: <UserPage />,
      layout: "/admin",
    },
    {
      path: "/ManageCoursePage",
      name: "Manage Course",
      icon: "nc-icon nc-book-bookmark",
      component: <ManageCoursePage />,
      layout: "/admin",
    },
    {
      path: "/BatchManager",
      name: "Batch Manage",
      icon: "nc-icon nc-watch-time",
      component: <BatchManager />,
      layout: "/admin",
    },
    {
      path: "/tables",
      name: "Manage Schedule",
      icon: "nc-icon nc-calendar-60",
      component: <TableList />,
      layout: "/admin",
    },
    {
      path: "/typography",
      name: "Manage Exam",
      icon: "nc-icon nc-ruler-pencil",
      component: <Typography />,
      layout: "/admin",
    },
    {
      path: "/Courses",
      name: "My Courses",
      icon: "nc-icon nc-single-copy-04",
      component: <Courses />,
      layout: "/admin",
    },
    {
      path: "/QandA",
      name: "Question and Answer",
      icon: "nc-icon nc-bullet-list-67",
      component: <QandA />,
      layout: "/admin",
    },
  ];

  // Conditionally include components based on user's category
  if (userCategory === 'Student') {
    routes.push({
      path: "/Liveclass",
      name: "My Classes",
      icon: "nc-icon nc-time-alarm",
      component: <Liveclass />,
      layout: "/admin",
    });
  } else if (userCategory === 'admin') {
    console.log(userCategory);
    routes.push({
      path: "/upgrade",
      name: "Manage Certification",
      icon: "nc-icon nc-trophy",
      component: <UpgradeToPro />,
      layout: "/admin",
    });
  }

  return (
    <div>
      {/* Render your routes here */}
    </div>
  );
}

export default routes;
