// ... (existing imports)

const Notifications = () => {
  const [tableData, setTableData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddFacultiesForm, setShowAddFacultiesForm] = useState(false);
  const [newFacultyData, setNewFacultyData] = useState({
    facultyName: '',
    facultyPhone: '',
    facultyEmail: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('http://localhost:3001/api/users');
        if (!response.ok) {
          throw new Error('Data could not be fetched!');
        }
        const data = await response.json();
        setTableData(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAddFaculties = async () => {
    setShowAddFacultiesForm(true);
  };

  const handleRemove = async (index) => {
    // ... (existing remove logic)
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFacultyData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSaveFaculties = async () => {
    try {
      // Example: Make a request to add faculties
      const response = await fetch('http://localhost:3001/api/addFaculties', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...newFacultyData,
          Category: 'Faculty', // Default category is set to 'Faculty'
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to add faculties to the server');
      }

      // Example: Handle the success response
      console.log('Faculties added successfully');
      setShowAddFacultiesForm(false); // Close the form after saving
    } catch (error) {
      console.error('Error adding faculties:', error);
      // Handle error as needed
    }
  };

  if (error) return <div>Error: {error}</div>;
  if (isLoading) return <div>Loading...</div>;

  return (
    <>
      <div className="content">
        <Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <CardTitle tag="h5">Faculties</CardTitle>
              </CardHeader>
              <CardBody>
                <Table>
                  <thead>
                    <tr>
                      <th>Username</th>
                      <th>Phone</th>
                      <th>Email</th>
                      <th>Remove</th>
                    </tr>
                  </thead>
                  <tbody>
                    {tableData.map((row, index) => (
                      <tr key={index}>
                        <td>{row.username}</td>
                        <td>{row.phone}</td>
                        <td>{row.email}</td>
                        <td>
                          <Button
                            color="danger"
                            onClick={() => handleRemove(index)}
                          >
                            Remove
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Card>
              <CardBody>
                <div className="places-buttons">
                  <Row>
                    <Col md="4">
                      <Button
                        block
                        color="success"
                        onClick={handleAddFaculties}
                      >
                        Add Faculties
                      </Button>
                    </Col>
                  </Row>
                </div>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>

      {/* Add Faculties Form */}
      {showAddFacultiesForm && (
        <Row>
          <Col md="12">
            <Card>
              <CardBody>
                <Form>
                  <FormGroup>
                    <Label for="facultyName">Faculty Name:</Label>
                    <Input
                      type="text"
                      id="facultyName"
                      name="facultyName"
                      value={newFacultyData.facultyName}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="facultyPhone">Faculty Phone:</Label>
                    <Input
                      type="text"
                      id="facultyPhone"
                      name="facultyPhone"
                      value={newFacultyData.facultyPhone}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="facultyEmail">Faculty Email:</Label>
                    <Input
                      type="email"
                      id="facultyEmail"
                      name="facultyEmail"
                      value={newFacultyData.facultyEmail}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  {/* Add more form fields as needed */}

                  <Button color="success" onClick={handleSaveFaculties}>
                    Save Faculties
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      )}
    </>
  );
};

export default Notifications;
