import React, { useState, useEffect } from 'react';
import Dashboard from "views/Dashboard.js";
import Notifications from "views/Notifications.js";
import Icons from "views/Icons.js";
import Typography from "views/Typography.js";
import TableList from "views/Tables.js";
import UserPage from "views/User.js";
import ManageCoursePage from "views/ManageCoursePage.js";
import BatchManager from "views/BatchManager.js";
import Courses from "views/Courses";
import Liveclass from "views/Liveclass";
import QandA from "views/QandA";
import UpgradeToPro from "views/Upgrade.js";

const useRoutes = () => {
  const [userType, setUserType] = useState('');
  const [userRoutes, setUserRoutes] = useState([]);

  useEffect(() => {
    const fetchUserType = async () => {
      try {
        const userEmail = sessionStorage.getItem('email');
        const response = await fetch(`http://localhost:3001/api/userType?email=${userEmail}`);
        const data = await response.json();
        setUserType(data.userType);
      } catch (error) {
        console.error('Error fetching user type:', error);
      }
    };

    fetchUserType();
  }, []); // Run the effect only once on component mount

  useEffect(() => {
    // Define routes based on user type
    if (userType === 'Student') {
      setUserRoutes([
        {
          path: "/Liveclass",
          name: "My Classes",
          icon: "nc-icon nc-time-alarm",
          component: <Liveclass />,
          layout: "/admin",
        },
        {
          path: "/Courses",
          name: "My Courses",
          icon: "nc-icon nc-single-copy-04",
          component: <Courses />,
          layout: "/admin",
        },
        {
          path: "/QandA",
          name: "Question and Answer",
          icon: "nc-icon nc-bullet-list-67",
          component: <QandA />,
          layout: "/admin",
        }
      ]);
    } else if (userType === 'admin') {
      setUserRoutes([
        {
          path: "/upgrade",
          name: "Manage Certification",
          icon: "nc-icon nc-trophy",
          component: <UpgradeToPro />,
          layout: "/admin",
        },
        {
          path: "/Icons",
          name: "Manage Student",
          icon: "nc-icon nc-single-02",
          component: <Icons />,
          layout: "/admin",
        },
        {
          path: "/notifications",
          name: "Manage Faculties",
          icon: "nc-icon nc-hat-3",
          component: <Notifications />,
          layout: "/admin",
        },
        {
          path: "/ManageCoursePage",
          name: "Manage Course",
          icon: "nc-icon nc-book-bookmark",
          component: <ManageCoursePage />,
          layout: "/admin",
        },
        {
          path: "/BatchManager",
          name: "Batch Manage",
          icon: "nc-icon nc-watch-time",
          component: <BatchManager />,
          layout: "/admin",
        },
        {
          path: "/tables",
          name: "Manage Schedule",
          icon: "nc-icon nc-calendar-60",
          component: <TableList />,
          layout: "/admin",
        },
        {
          path: "/typography",
          name: "Manage Exam",
          icon: "nc-icon nc-ruler-pencil",
          component: <Typography />,
          layout: "/admin",
        }
      ]);
    } else {
      // Default route if the user category is neither 'Student' nor 'admin'
      setUserRoutes([
        {
          path: "/dashboard",
          name: "Dashboard",
          icon: "nc-icon nc-chart-bar-32",
          component: <Dashboard />,
          layout: "/admin",
        },
        {
          path: "/user-page",
          name: "Profile",
          icon: "nc-icon nc-bookmark-2",
          component: <UserPage />,
          layout: "/admin",
        }
      ]);
    }
  }, [userType]);

  return userRoutes;
};

export default useRoutes;
