import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Registration.css';

function Registration() {
  const [formData, setFormData] = useState({
    username: '',
    phone: '',
    email: '',
    password: '',
    category: '',
    experience: '',
    subject: '',
  });

  const navigate = useNavigate();

  const handleInputChange = (fieldName, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [fieldName]: value,
    }));
  };

  const handleRegistration = async () => {
    try {
      const response = await axios.post("http://localhost:3001/Registration", formData);

      if (response.data.message === "User registered successfully") {
        navigate("/");
      } else if (response.data.message === "exist") {
        alert("User already exists");
      } else if (response.data.message === "notexist") {
        alert("User does not exist");
      }
    } catch (error) {
      alert("Wrong details");
      console.error(error);
    }
  };

  return (
    <div>
      {/* Your header and navigation components */}
      <div className="content-container">
        {/* Your book image and login container */}
        <div className="login-container">
          <div className="login-form">
            <input
              type="text"
              placeholder="Username"
              value={formData.username}
              onChange={(e) => handleInputChange('username', e.target.value)}
            />
            <input
              type="text"
              placeholder="Phone"
              value={formData.phone}
              onChange={(e) => handleInputChange('phone', e.target.value)}
            />
            <input
              type="text"
              placeholder="Email"
              value={formData.email}
              onChange={(e) => handleInputChange('email', e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={formData.password}
              onChange={(e) => handleInputChange('password', e.target.value)}
            />
            <select
              value={formData.category}
              onChange={(e) => handleInputChange('category', e.target.value)}
            >
              <option value="">Select Category</option>
              <option value="Student">Student</option>
              <option value="Faculty">Faculty</option>
            </select>
            {formData.category === 'Faculty' && (
              <>
                <input
                  type="text"
                  placeholder="Experience"
                  value={formData.experience}
                  onChange={(e) => handleInputChange('experience', e.target.value)}
                />
                <select
                  value={formData.subject}
                  onChange={(e) => handleInputChange('subject', e.target.value)}
                >
                  <option value="">Select Subject</option>
                  {/* Add your subject options here */}
                </select>
              </>
            )}
            <button onClick={handleRegistration}>Register</button>
            <p>Already have an account? <Link to="/">Login</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Registration;
