import React, { useState } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
  FormGroup,
  Label,
  Input,
  Form,
  Button,
} from "reactstrap";

function Tables() {
  const [subject, setSubject] = useState("");
  const [faculty, setFaculty] = useState("");
  const [link, setLink] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to save batch details to your database or state
    // You can generate a unique batch name here
    console.log("Batch added:", { subject, faculty, link, date });
  };

  return (
    <>
      <div className="content">
        <Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <CardTitle tag="h4">Manage Schedule</CardTitle>
              </CardHeader>
              <CardBody>
                <Form onSubmit={handleSubmit}>
                  <FormGroup>
                    <Label for="subject">Subject</Label>
                    <Input
                      type="select"
                      name="subject"
                      id="subject"
                      value={subject}
                      onChange={(e) => setSubject(e.target.value)}
                    >
                      {/* Populate options for subjects */}
                      <option value="math">Math</option>
                      <option value="science">Science</option>
                      {/* Add more options as needed */}
                    </Input>
                  </FormGroup>
                  <FormGroup>
                    <Label for="faculty">Faculty</Label>
                    <Input
                      type="select"
                      name="faculty"
                      id="faculty"
                      value={faculty}
                      onChange={(e) => setFaculty(e.target.value)}
                    >
                      {/* Populate options for faculties */}
                      <option value="john">John</option>
                      <option value="jane">Jane</option>
                      {/* Add more options as needed */}
                    </Input>
                  </FormGroup>
                  <FormGroup>
                    <Label for="link">Link</Label>
                    <Input
                      type="text"
                      name="link"
                      id="link"
                      value={link}
                      onChange={(e) => setLink(e.target.value)}
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="date">Date</Label>
                    <Input
                      type="date"
                      name="date"
                      id="date"
                      value={date}
                      onChange={(e) => setDate(e.target.value)}
                    />
                  </FormGroup>
                  <Button color="primary" type="submit">
                    Add Batch
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default Tables;
