// Function to get user's category from session
function getUserCategory() {
  return sessionStorage.getItem("category");
}

const userCategory = getUserCategory();
// Initial routes without conditional components
let routes = [];

// Conditionally include components based on user's category
if (userCategory === 'Student') {
  routes.push(
    {
      path: "/dashboard",
      name: "Dashboard",
      icon: "nc-icon nc-chart-bar-32",
      component: "Dashboard",
      layout: "/admin",
    },
    {
      path: "/user-page",
      name: "Profile",
      icon: "nc-icon nc-bookmark-2",
      component: "UserPage",
      layout: "/admin",
      category: "admin"
    },
    {
      path: "/Liveclass",
      name: "My Classes",
      icon: "nc-icon nc-time-alarm",
      component: "Liveclass",
      layout: "/admin",
    },
    {
      path: "/Courses",
      name: "My Courses",
      icon: "nc-icon nc-single-copy-04",
      component: "Courses",
      layout: "/admin",
    },
    {
      path: "/QandA",
      name: "Question and Answer",
      icon: "nc-icon nc-bullet-list-67",
      component: "QandA",
      layout: "/admin",
    }
  );
} else if (userCategory === 'admin') {
  routes.push(
    {
      path: "/dashboard",
      name: "Dashboard",
      icon: "nc-icon nc-chart-bar-32",
      component: "Dashboard",
      layout: "/admin",
    },
    {
      path: "/user-page",
      name: "Profile",
      icon: "nc-icon nc-bookmark-2",
      component: "UserPage",
      layout: "/admin",
      category: "admin"
    },
    {
      path: "/upgrade",
      name: "Manage Certification",
      icon: "nc-icon nc-trophy",
      component: "UpgradeToPro",
      layout: "/admin",
    },
    {
      path: "/Icons",
      name: "Manage Student",
      icon: "nc-icon nc-single-02",
      component: "Icons",
      layout: "/admin",
    },
    {
      path: "/notifications",
      name: "Manage Faculties",
      icon: "nc-icon nc-hat-3",
      component: "Notifications",
      layout: "/admin",
    },
    {
      path: "/ManageCoursePage",
      name: "Manage Course",
      icon: "nc-icon nc-book-bookmark",
      component: "ManageCoursePage",
      layout: "/admin",
    },
    {
      path: "/BatchManager",
      name: "Batch Manage",
      icon: "nc-icon nc-watch-time",
      component: "BatchManager",
      layout: "/admin",
    },
    {
      path: "/tables",
      name: "Manage Schedule",
      icon: "nc-icon nc-calendar-60",
      component: "TableList",
      layout: "/admin",
    },
    {
      path: "/typography",
      name: "Manage Exam",
      icon: "nc-icon nc-ruler-pencil",
      component: "Typography",
      layout: "/admin",
    }
  );
}

export default routes;
