import React, { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  FormGroup,
  Label,
  Input,
  Form,
  Button,
} from "reactstrap";

function Tables() {
  const [subjects, setSubjects] = useState([]);
  const [faculties, setFaculties] = useState([]);
  const [subject, setSubject] = useState("");
  const [faculty, setFaculty] = useState("");
  const [link, setLink] = useState("");
  const [date, setDate] = useState("");

  useEffect(() => {
    fetchSubjects();
    fetchFaculties();
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/subjects");
      const data = await response.json();
     // console.log("Subjects:", data); // Log data received from the API
      setSubjects(data);
    } catch (error) {
      console.error("Error fetching subjects:", error);
    }
  };

  const fetchFaculties = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/faculties");
      const data = await response.json();
     // console.log("Faculties:", data); // Log data received from the API
      setFaculties(data);
    } catch (error) {
      console.error("Error fetching faculties:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      // Make a POST request to save the batch details to your backend
      const response = await fetch('http://localhost:3001/api/batches', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ subject, faculty, link, date }),
      });
  
      if (response.ok) {
        console.log('Batch added successfully!');
        // Optionally, you can clear the form fields here
        setSubject('');
        setFaculty('');
        setLink('');
        setDate('');
      } else {
        console.error('Failed to add batch:', response.statusText);
      }
    } catch (error) {
      console.error('Error adding batch:', error.message);
    }
  };
  

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Manage Schedule</CardTitle>
            </CardHeader>
            <CardBody>
              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label for="subject">Subject</Label>
                  <Input
                    type="select"
                    name="subject"
                    id="subject"
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)}
                  >
                    <option value="">Select Subject</option>
                    {subjects.map((subject) => (
                      <option key={subject._id} value={subject.subject}>
                        {subject.subject}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
                <FormGroup>
                  <Label for="faculty">Faculty</Label>
                  <Input
                    type="select"
                    name="faculty"
                    id="faculty"
                    value={faculty}
                    onChange={(e) => setFaculty(e.target.value)}
                  >
                    <option value="">Select Faculty</option>
                    {faculties.map((fac) => (
                      <option key={fac._id} value={fac.username}>
                        {fac.username}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
                <FormGroup>
                  <Label for="link">Link</Label>
                  <Input
                    type="text"
                    name="link"
                    id="link"
                    value={link}
                    onChange={(e) => setLink(e.target.value)}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="date">Date</Label>
                  <Input
                    type="date"
                    name="date"
                    id="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                  />
                </FormGroup>
                <Button color="primary" type="submit">
                  Add Batch
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Tables;
