import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, CardHeader, CardBody, CardTitle, Row, Col, Table, Button, Form, FormGroup, Label, Input } from 'reactstrap';

// Dummy data for enrolled students
const enrolledStudentsData = [
  { id: 1, name: 'John Doe' },
  { id: 2, name: 'Jane Smith' },
  { id: 3, name: 'Michael Johnson' },
  { id: 4, name: 'Emily Brown' },
  { id: 5, name: 'William Wilson' }
];

const TableComponent = ({ batches, deleteBatch }) => {
  return (
    <div>
      <Table>
        <thead>
          <tr>
            <th>Batch ID</th>
            <th>Subject</th>
            <th>Enrolled Students</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {batches.map((batch) => (
            <tr key={batch._id}>
              <td>{batch.batchID}</td>
              <td>{batch.subject}</td>
              <td>{batch.enrolledStudents.join(', ')}</td>
              <td>
                <Button color="danger" onClick={() => deleteBatch(batch._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

const BatchManagerPage = () => {
  const [batches, setBatches] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [newBatch, setNewBatch] = useState({
    batchID: '',
    subject: '',
    enrolledStudents: []
  });

  useEffect(() => {
    fetchBatches();
  }, []);

  const fetchBatches = async () => {
    try {
      const response = await axios.get('http://localhost:3001/fetchBatches');
      setBatches(response.data);
    } catch (error) {
      console.error('Error fetching batches:', error);
    }
  };

  const deleteBatch = async (batchID) => {
    try {
      await axios.delete(`http://localhost:3001/deleteBatch/${batchID}`);
      setBatches(batches.filter(batch => batch._id !== batchID));
    } catch (error) {
      console.error('Error deleting batch:', error);
    }
  };

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
    const batchID = `${event.target.value}_batch_${String(batches.length + 1).padStart(4, '0')}`;
    setNewBatch(prevBatch => ({ ...prevBatch, batchID, subject: event.target.value }));
  };

  const handleStudentChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedStudents([...selectedStudents, value]);
    } else {
      setSelectedStudents(selectedStudents.filter(student => student !== value));
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:3001/addBatch', newBatch);
      setBatches([...batches, newBatch]);
      setIsFormOpen(false);
      setNewBatch({ batchID: '', subject: '', enrolledStudents: [] });
    } catch (error) {
      console.error('Error adding batch:', error);
    }
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h5">Manage Batches</CardTitle>
              <p className="card-category">Add and manage batches</p>
            </CardHeader>
            <CardBody>
              <Button color="primary" onClick={() => setIsFormOpen(true)}>Add Batch</Button>
              {isFormOpen && (
                <Form>
                  <Row form>
                    <Col md={6}>
                      <FormGroup>
                        <Label for="subject">Select Subject:</Label>
                        <Input type="select" id="subject" value={selectedSubject} onChange={handleSubjectChange}>
                          <option value="">Select</option>
                          <option value="Math">Math</option>
                          <option value="Science">Science</option>
                          {/* Add more options based on backend data if needed */}
                        </Input>
                      </FormGroup>
                    </Col>
                    <Col md={6}>
                      <FormGroup>
                        <Label for="batchID">Batch ID:</Label>
                        <Input type="text" id="batchID" value={newBatch.batchID} disabled />
                      </FormGroup>
                    </Col>
                  </Row>
                  <FormGroup>
                    <Label>Enroll Students:</Label>
                    <FormGroup check>
                      {selectedSubject &&
                        enrolledStudentsData.map(student => (
                          <Label key={student.id} check>
                            <Input type="checkbox" value={student.name} onChange={handleStudentChange} />{' '}
                            {student.name}
                          </Label>
                        ))}
                    </FormGroup>
                  </FormGroup>
                  <Button color="success" onClick={handleSubmit} disabled={!selectedSubject || selectedStudents.length === 0}>Add</Button>
                </Form>
              )}
              <TableComponent
                batches={batches}
                deleteBatch={deleteBatch}
              />
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default BatchManagerPage;
