import React, { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Table,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
} from 'reactstrap';

const TableComponent = ({ tableData, handleRemove }) => (
  <Table>
    <thead>
      <tr>
        <th>Username</th>
        <th>Phone</th>
        <th>Email</th>
        <th>Remove</th>
      </tr>
    </thead>
    <tbody>
      {tableData.map((row, index) => (
        <tr key={index}>
          <td>{row.username}</td>
          <td>{row.phone}</td>
          <td>{row.email}</td>
          <td>
            <Button color="danger" onClick={() => handleRemove(row._id, index)}>
              Remove
            </Button>
          </td>
        </tr>
      ))}
    </tbody>
  </Table>
);

const Icons = () => {
  const [tableData, setTableData] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [newUserData, setNewUserData] = useState({ name: '', phone: '', email: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('http://localhost:3001/api/users');
        if (!response.ok) {
          throw new Error('Could not fetch data!');
        }
        const data = await response.json();
        setTableData(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchUsers();
  }, []);

  const handleRemove = async (userId, index) => {
    setIsLoading(true);
    try {
      const response = await fetch(`http://localhost:3001/api/removeUser/${userId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to remove user from the server');
      }

      const updatedData = [...tableData];
      updatedData.splice(index, 1);
      setTableData(updatedData);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUserData(prevData => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/addUser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newUserData),
      });

      if (!response.ok) {
        throw new Error('Failed to add user to the server');
      }

      setNewUserData({ name: '', phone: '', email: '' }); // Reset form
      setIsFormOpen(false); // Close the form
      await fetch('http://localhost:3001/show') // Refresh user data
      console.log(setNewUserData)
        .then((res) => res.json())
        .then(setTableData);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h5">User Table</CardTitle>
              <p className="card-category">Displaying user data in a table</p>
            </CardHeader>
            <CardBody>
              <Button color="primary" onClick={() => setIsFormOpen(!isFormOpen)}>
                Add Student
              </Button>

              {isFormOpen && (
                <Form>
                  <FormGroup>
                    <Label for="name">Name:</Label>
                    <Input
                      type="text"
                      id="name"
                      name="name"
                      value={newUserData.name}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="phone">Phone:</Label>
                    <Input
                      type="text"
                      id="phone"
                      name="phone"
                      value={newUserData.phone}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="email">Email:</Label>
                    <Input
                      type="email"
                      id="email"
                      name="email"
                      value={newUserData.email}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <Button color="success" onClick={handleSave}>
                    Save
                  </Button>
                </Form>
              )}

              <TableComponent tableData={tableData} handleRemove={handleRemove} />
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Icons;
