import React, { useState } from "react";

function ExamForm() {
  const [batchId, setBatchId] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [questionsFile, setQuestionsFile] = useState(null);
  const [exams, setExams] = useState([]);

  const handleAddExam = () => {
    // Create a new exam object with form data
    const newExam = {
      batchId,
      date,
      time,
      questionsFile
    };

    // Add the new exam to the list of exams
    setExams([...exams, newExam]);

    // Clear form fields after adding the exam
    setBatchId("");
    setDate("");
    setTime("");
    setQuestionsFile(null);
  };

  return (
    <div>
      <h2>Add Exam</h2>
      <div>
        <label>Batch ID:</label>
        <input
          type="text"
          value={batchId}
          onChange={(e) => setBatchId(e.target.value)}
        />
      </div>
      <div>
        <label>Date:</label>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
      </div>
      <div>
        <label>Time:</label>
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
      </div>
      <div>
        <label>Upload Questions:</label>
        <input
          type="file"
          onChange={(e) => setQuestionsFile(e.target.files[0])}
        />
      </div>
      <button onClick={handleAddExam}>Add Exam</button>

      {/* Display added exams in a table */}
      <h2>Added Exams</h2>
      <table>
        <thead>
          <tr>
            <th>Batch ID</th>
            <th>Date</th>
            <th>Time</th>
            <th>Questions File</th>
          </tr>
        </thead>
        <tbody>
          {exams.map((exam, index) => (
            <tr key={index}>
              <td>{exam.batchId}</td>
              <td>{exam.date}</td>
              <td>{exam.time}</td>
              <td>{exam.questionsFile.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ExamForm;
