import React, { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Table,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from 'reactstrap';
import NotificationAlert from 'react-notification-alert';

const Notifications = () => {
  const [tableData, setTableData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const notificationAlert = React.useRef();

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('http://localhost:3001/api/users');
        if (!response.ok) {
          throw new Error('Data could not be fetched!');
        }
        const data = await response.json();
        setTableData(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const notify = (place, username) => {
    const type = 'success';
    const options = {
      place: place,
      message: (
        <div>
          <div>
            User <b>{username}</b> added successfully.
          </div>
        </div>
      ),
      type: type,
      icon: 'nc-icon nc-bell-55',
      autoDismiss: 7,
    };
    notificationAlert.current.notificationAlert(options);
  };

  const handleRemove = async (index) => {
    const updatedTableData = [...tableData];
    const removedItem = updatedTableData.splice(index, 1)[0];
    setTableData(updatedTableData);

    try {
      const response = await fetch(
        `http://localhost:3001/api/removeUser/${removedItem._id}`,
        {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      if (!response.ok) {
        throw new Error('Failed to remove user from the server');
      }

      console.log(`User ${removedItem.username} removed successfully`);
    } catch (error) {
      console.error('Error removing user:', error);
    }

    onRemove(updatedTableData);
  };

  if (error) return <div>Error: {error}</div>;
  if (isLoading) return <div>Loading...</div>;

  return (
    <>
      <div className="content">
        <NotificationAlert ref={notificationAlert} />
        <Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <CardTitle tag="h5">Notifications</CardTitle>
              </CardHeader>
              <CardBody>
                <Table>
                  <thead>
                    <tr>
                      <th>Username</th>
                      <th>Phone</th>
                      <th>Email</th>
                      <th>Remove</th>
                    </tr>
                  </thead>
                  <tbody>
                    {tableData.map((row, index) => (
                      <tr key={index}>
                        <td>{row.username}</td>
                        <td>{row.phone}</td>
                        <td>{row.email}</td>
                        <td>
                          <Button
                            color="danger"
                            onClick={() => handleRemove(index)}
                          >
                            Remove
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Card>
              <CardBody>
                <div className="places-buttons">
                  <Row>
                    <Col md="4">
                      <Button
                        block
                        color="success"
                        onClick={() => notify('tc', 'New User')}
                      >
                        Add User Notification
                      </Button>
                    </Col>
                  </Row>
                </div>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
};

export default Notifications;
