import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Registration.css';

function Registration() {
  const [formData, setFormData] = useState({
    username: '',
    phone: '',
    email: '',
    password: '',
    category: '',
    experience: '', // New field for experience
    subject: '', // New field for subject
  });

  const navigate = useNavigate();

  const handleInputChange = (fieldName, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [fieldName]: value,
    }));
  };

  const handleRegistration = async () => {
    try {
      const response = await axios.post("http://localhost:3001/Registration", formData);

      if (response.data.message === "User registered successfully") {
        navigate("/");
      } else if (response.data.message === "exist") {
        alert("User already exists");
      } else if (response.data.message === "notexist") {
        alert("User does not exist");
      }
    } catch (error) {
      alert("Wrong details");
      console.error(error);
    }
  };

  return (
    <div>
      <header className="header">
        <div className="logo-container">
          <img src="data:image/png;base64," alt="Logo" className="logo" />
          <h1>Library Management System</h1>
        </div>
      </header>
      <nav className="navbar">
        <a href="#home">Home</a>
        <a href="#gallery">Gallery</a>
        <a href="#how-it-works">How It Works</a>
        <a href="#plans">Plans</a>
        <a href="#about">About</a>
        <a href="#contact-us">Contact Us</a>
      </nav>
      <div className="content-container">
        <div className="book-image">
          <img src="https://i0.wp.com/stanzaliving.wpcomstaging.com/wp-content/uploads/2022/04/fc23e-library-in-jaipur.jpg?fit=1000%2C667&ssl=1" alt="Person touching a virtual screen" height="600" width="900" />
        </div>
        <div className="login-container">
          <div className="login-form">
            <form>
              <label>Username</label>
              <input type="text" value={formData.username} onChange={(e) => handleInputChange('username', e.target.value)} />
              <label>Phone</label>
              <input type="text" value={formData.phone} onChange={(e) => handleInputChange('phone', e.target.value)} />
              <label>Email</label>
              <input type="email" value={formData.email} onChange={(e) => handleInputChange('email', e.target.value)} />
              <label>Password</label>
              <input type="password" value={formData.password} onChange={(e) => handleInputChange('password', e.target.value)} />
              <label>Category</label>
              <select value={formData.category} onChange={(e) => handleInputChange('category', e.target.value)}>
                <option value="">Select</option>
                <option value="Student">Student</option>
                <option value="Faculty">Faculty</option>
              </select>
              {formData.category === 'Faculty' && ( // Render the additional fields for Faculty category
                <>
                  <label>Experience</label>
                  <input type="text" value={formData.experience} onChange={(e) => handleInputChange('experience', e.target.value)} />
                  <label>Subject</label>
                  <input type="text" value={formData.subject} onChange={(e) => handleInputChange('subject', e.target.value)} />
                </>
              )}
              <br />
              <button type="button" onClick={handleRegistration}>Register</button>
              <div className="mt-3 text-center">
                <p className="small text-muted">
                  Already have an account? <Link to="/">Login here</Link>
                </p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Registration;
