import React, { useEffect, useState } from "react";
import axios from 'axios';
import {
  Card,
  CardBody,
  CardFooter,
  Row,
  Col,
} from "reactstrap";

function User() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Update the backend URL to match your backend server
    const backendUrl = 'http://localhost:3001'; // Update this URL as needed

    // Fetch user data from the backend
    axios.get(`${backendUrl}/api/user`)
      .then(response => {
        setUser(response.data.user);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data:', error.message);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <>
      <div className="content">
        <Row>
          <Col md="4">
            <Card className="card-user">
              <div className="image">
                <img alt="..." src={require("assets/img/damir-bosnjak.jpg")} />
              </div>
              <CardBody>
                <div className="author">
                  <a href="#pablo" onClick={(e) => e.preventDefault()}>
                    <img
                      alt="..."
                      className="avatar border-gray"
                      src={require("assets/img/mike.jpg")}
                    />
                    <h5 className="title">{user ? user.username : ''}</h5>
                  </a>
                  <p className="description">Name: {user ? user.username : ''}</p>
                  <p className="description">Email: {user ? user.email : ''}</p>
                  <p className="description">Phone: {user ? user.phone : ''}</p>
                </div>
              </CardBody>
              <CardFooter>
                <hr />
                <div className="button-container">
                  {/* You can add additional buttons or components here */}
                </div>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default User;
