import React, { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  FormGroup,
  Label,
  Input,
  Form,
  Button,
  Table,
} from "reactstrap";
import validator from 'validator';

function Tables() {
  const [faculties, setFaculties] = useState([]);
  const [faculty, setFaculty] = useState("");
  const [link, setLink] = useState("");
  const [date, setDate] = useState("");
  const [batches, setBatches] = useState([]);
  const [selectedBatchId, setSelectedBatchId] = useState(""); // State to store selected batch ID
  const [batchData, setBatchData] = useState(null); // State to store data of the selected batch

  useEffect(() => {
    fetchFaculties();
    fetchBatches();
  }, []);

  useEffect(() => {
    if (selectedBatchId) {
      fetchBatchData(selectedBatchId);
    }
  }, [selectedBatchId]);

  const fetchFaculties = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/faculties");
      const data = await response.json();
      setFaculties(data);
    } catch (error) {
      console.error("Error fetching faculties:", error);
    }
  };
  
  const fetchBatches = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/batches");
      const data = await response.json();
      setBatches(data);
    } catch (error) {
      console.error("Error fetching batches:", error);
    }
  };

  const fetchBatchData = async (batchId) => {
    try {
      const response = await fetch(`http://localhost:3001/api/batch/${batchId}`);
      const data = await response.json();
      setBatchData(data);
    } catch (error) {
      console.error(`Error fetching batch data for ID ${batchId}:`, error);
      setBatchData(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validator.isURL(link)) {
      console.error('Invalid URL');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/api/schedules', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ selectedBatchId, faculty, link, date }),
      });

      if (response.ok) {
        console.log('Schedule added successfully!');
        setFaculty('');
        setLink('');
        setDate('');
        setSelectedBatchId('');
        fetchBatches();
      } else {
        console.error('Failed to add schedule:', response.statusText);
      }
    } catch (error) {
      console.error('Error adding schedule:', error.message);
    }
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Manage Schedule</CardTitle>
            </CardHeader>
            <CardBody>
              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label for="selectedBatchId">Select Batch ID</Label>
                  <Input
                    type="select"
                    name="selectedBatchId"
                    id="selectedBatchId"
                    value={selectedBatchId}
                    onChange={(e) => setSelectedBatchId(e.target.value)}
                  >
                    <option value="">Select Batch ID</option>
                    {batches.map((batch) => (
                      <option key={batch._id} value={batch._id}>
                        {batch._id}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
                {batchData && (
                  <div>
                    <h4>Batch Details</h4>
                    <p>Batch Name: {batchData.name}</p>
                    <p>Other Details: {batchData.otherDetails}</p>
                  </div>
                )}
                <FormGroup>
                  <Label for="faculty">Faculty</Label>
                  <Input
                    type="select"
                    name="faculty"
                    id="faculty"
                    value={faculty}
                    onChange={(e) => setFaculty(e.target.value)}
                  >
                    <option value="">Select Faculty</option>
                    {faculties.map((fac) => (
                      <option key={fac._id} value={fac.username}>
                        {fac.username}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
                <FormGroup>
                  <Label for="link">Link</Label>
                  <Input
                    type="text"
                    name="link"
                    id="link"
                    value={link}
                    onChange={(e) => setLink(e.target.value)}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="date">Date</Label>
                  <Input
                    type="date"
                    name="date"
                    id="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                  />
                </FormGroup>
                <Button color="primary" type="submit">
                  Add Schedule
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Tables;
