// Icons.js
import React, { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Table,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
} from 'reactstrap';

const TableComponent = ({ tableData, onRemove }) => {
  const handleRemove = async (id, index) => {
    try {
      const response = await fetch(`http://localhost:3001/api/removeUser/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to remove user from the server');
      }
      onRemove(index);
    } catch (error) {
      console.error('Error removing user:', error);
    }
  };

  return (
    <Table>
      <thead>
        <tr>
          <th>Username</th>
          <th>Phone</th>
          <th>Email</th>
          <th>Remove</th>
        </tr>
      </thead>
      <tbody>
        {tableData.map((row, index) => (
          <tr key={row._id}>
            <td>{row.name}</td>
            <td>{row.phone}</td>
            <td>{row.email}</td>
            <td>
              <Button color="danger" onClick={() => handleRemove(row._id, index)}>
                Remove
              </Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

const Icons = () => {
  const [tableData, setTableData] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [newUserData, setNewUserData] = useState({ name: '', phone: '', email: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUsers = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/users');
      if (!response.ok) {
        throw new Error('Could not fetch data!');
      }
      const data = await response.json();
      setTableData(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleRemove = (index) => {
    const updatedData = [...tableData];
    updatedData.splice(index, 1);
    setTableData(updatedData);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUserData(prevData => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/addUser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newUserData),
      });

      if (!response.ok) {
        throw new Error('Failed to add user to the server');
      }

      await fetchUsers(); // Refresh the user list
      setIsFormOpen(false); // Close the form
    } catch (error) {
      console.error('Error adding user:', error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h5">User Table</CardTitle>
              <p className="card-category">Displaying user data in a table</p>
            </CardHeader>
            <CardBody>
              <Button color="primary" onClick={() => setIsFormOpen(!isFormOpen)}>
                {isFormOpen ? 'Close Form' : 'Add User'}
              </Button>

              {isFormOpen && (
                <Form>
                  <FormGroup>
                    <Label for="name">Name</Label>
                    <Input type="text" name="name" value={newUserData.name} onChange={handleInputChange} />
                  </FormGroup>
                  <FormGroup>
                    <Label for="phone">Phone</Label>
                    <Input type="text" name="phone" value={newUserData.phone} onChange={handleInputChange} />
                  </FormGroup>
                  <FormGroup>
                    <Label for="email">Email</Label>
                    <Input type="email" name="email" value={newUserData.email} onChange={handleInputChange} />
                  </FormGroup>
                  <Button color="success" onClick={handleSave}>
                    Save
                  </Button>
                </Form>
              )}

              <TableComponent tableData={tableData} onRemove={handleRemove} />
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Icons;
