import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, CardHeader, CardBody, CardTitle, Row, Col, Table, Button, Form, FormGroup, Label, Input } from 'reactstrap';

const TableComponent = ({ batches, deleteBatch }) => {
  return (
    <div>
      <Table>
        <thead>
          <tr>
            <th>Batch ID</th>
            <th>Subject</th>
            <th>Enrolled Students</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {batches.map((batch) => (
            <tr key={batch.batchID}>
              <td>{batch.batchID}</td>
              <td>{batch.subject}</td>
              <td>{batch.enrolledStudents.join(', ')}</td>
              <td>
                <Button color="danger" onClick={() => deleteBatch(batch.batchID)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

const BatchManagerPage = () => {
  const [batches, setBatches] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [newBatch, setNewBatch] = useState({
    batchID: '',
    subject: '',
    enrolledStudents: []
  });

  useEffect(() => {
    fetchSubjects();
    fetchBatches();
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await axios.get('http://localhost:3001/BatchManager');
      setSubjects(response.data);
    } catch (error) {
      console.error('Error fetching subjects:', error);
    }
  };

  const fetchBatches = async () => {
    try {
      const response = await axios.get('http://localhost:3001/BatchManager');
      setBatches(response.data);
    } catch (error) {
      console.error('Error fetching batches:', error);
    }
  };

  const deleteBatch = async (batchID) => {
    try {
      await axios.delete(`http://localhost:3001/batches/${batchID}`);
      setBatches(batches.filter(batch => batch.batchID !== batchID));
    } catch (error) {
      console.error('Error deleting batch:', error);
    }
  };

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
    const batchID = `${event.target.value}_batch_${Math.floor(1000 + Math.random() * 9000)}`;
    setNewBatch(prevBatch => ({ ...prevBatch, batchID, subject: event.target.value }));
  };

  const handleCheckboxChange = (studentID) => {
    const updatedStudents = selectedStudents.includes(studentID)
      ? selectedStudents.filter(id => id !== studentID)
      : [...selectedStudents, studentID];
    setSelectedStudents(updatedStudents);
    setNewBatch(prevBatch => ({ ...prevBatch, enrolledStudents: updatedStudents }));
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:3001/BatchManager', newBatch);
      setBatches([...batches, newBatch]);
      setIsFormOpen(false);
      setNewBatch({ batchID: '', subject: '', enrolledStudents: [] });
    } catch (error) {
      console.error('Error adding batch:', error);
    }
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h5">Manage Batches</CardTitle>
              <p className="card-category">Add and manage batches</p>
            </CardHeader>
            <CardBody>
              <Button color="primary" onClick={() => setIsFormOpen(true)}>Add Batch</Button>
              {isFormOpen && (
                <Form>
                  <Row form>
                    <Col md={6}>
                      <FormGroup>
                        <Label for="subject">Select Subject:</Label>
                        <Input type="select" id="subject" value={selectedSubject} onChange={handleSubjectChange}>
                          <option value="">Select</option>
                          {subjects.map(subject => (
                            <option key={subject} value={subject}>{subject}</option>
                          ))}
                        </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                  <FormGroup>
                    <Label>Selected Subject: {selectedSubject}</Label>
                  </FormGroup>
                  <FormGroup>
                    <Label>Batch ID: {newBatch.batchID}</Label>
                  </FormGroup>
                  <FormGroup>
                    <Label>Enroll Students:</Label>
                    {selectedSubject && subjects.find(sub => sub === selectedSubject) &&
                      subjects[selectedSubject].map(student => (
                        <div key={student.id}>
                          <input
                            type="checkbox"
                            id={student.id}
                            checked={selectedStudents.includes(student.id)}
                            onChange={() => handleCheckboxChange(student.id)}
                          />
                          <label htmlFor={student.id}>{student.name}</label>
                        </div>
                      ))}
                  </FormGroup>
                  <Button color="success" onClick={handleSubmit}>Add</Button>
                </Form>
              )}
              <TableComponent
                batches={batches}
                deleteBatch={deleteBatch}
              />
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default BatchManagerPage;
