import React, { useEffect, useState } from "react";
import axios from 'axios';
import {
  Card,
  CardBody,
  CardFooter,
  Row,
  Col,
} from "reactstrap";

function User() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Update the backend URL to match your backend server
    const backendUrl = 'http://localhost:3001'; // Update this URL as needed

    // Fetch user data from the backend
    axios.get(`${backendUrl}/profile`)
      .then(response => {
        setUser(response.data.user);
      })
      .catch(error => {
        console.error('Error fetching user data:', error.message);
      });
  }, []);

  return (
    <>
      <div className="content">
        <Row>
          <Col md="4">
            {user && (
              <Card className="card-user">
                <div className="image">
                  <img alt="..." src={require("assets/img/damir-bosnjak.jpg")} />
                </div>
                <CardBody>
                  <div className="author">
                    <a href="#pablo" onClick={(e) => e.preventDefault()}>
                      <img
                        alt="..."
                        className="avatar border-gray"
                        src={require("assets/img/mike.jpg")}
                      />
                      <h5 className="title">{user.username}</h5>
                    </a>
                    <p className="description">Name: {user.username}</p>
                    <p className="description">Email: {user.email}</p>
                    <p className="description">Phone: {user.phone}</p>
                  </div>
                </CardBody>
                <CardFooter>
                  <hr />
                  <div className="button-container">
                    {/* You can add additional buttons or components here */}
                  </div>
                </CardFooter>
              </Card>
            )}
          </Col>
        </Row>
      </div>
    </>
  );
}

export default User;
