import React, { useEffect, useState } from "react";
import axios from 'axios';
import {
  Card,
  CardBody,
  CardFooter,
  Row,
  Col,
} from "reactstrap";

function User() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Consider using environment variables for API URLs
    const backendUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:3001';

    // Fetch user data from the backend using template literals for URL
    axios.get(`${backendUrl}/api/user`)
      .then(response => {
        setUser(response.data.user);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  if (!user) return <div>Loading...</div>; // Added a simple loading state

  return (
    <div className="content">
      <Row>
        <Col md="4">
          <Card className="card-user">
            <div className="image">
              <img alt="..." src={require("assets/img/damir-bosnjak.jpg")} />
            </div>
            <CardBody>
              <div className="author">
                <a href="#pablo" onClick={(e) => e.preventDefault()}>
                  <img
                    alt="..."
                    className="avatar border-gray"
                    src={require("assets/img/mike.jpg")}
                  />
                  <h5 className="title">{user.username}</h5>
                </a>
                <p className="description">Name: {user.username}</p>
                <p className="description">Email: {user.email}</p>
                <p className="description">Phone: {user.phone}</p>
              </div>
            </CardBody>
            <CardFooter>
              <hr />
              <div className="button-container">
                {/* This space can be used for additional buttons or information */}
              </div>
            </CardFooter>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default User;
