import React, { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  FormGroup,
  Label,
  Input,
  Form,
  Button,
  Table,
} from "reactstrap";
import validator from 'validator';

function Tables() {
  const [subjects, setSubjects] = useState([]);
  const [faculties, setFaculties] = useState([]);
  const [subject, setSubject] = useState("");
  const [faculty, setFaculty] = useState("");
  const [link, setLink] = useState("");
  const [date, setDate] = useState("");
  const [batches, setBatches] = useState([]);
  const [batchName, setBatchName] = useState('');

  useEffect(() => {
    fetchSubjects();
    fetchFaculties();
    fetchBatches();
    generateBatchName(); // Generate batch name on component mount
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/subjects");
      const data = await response.json();
      setSubjects(data);
    } catch (error) {
      console.error("Error fetching subjects:", error);
    }
  };

  const fetchFaculties = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/faculties");
      const data = await response.json();
      setFaculties(data);
    } catch (error) {
      console.error("Error fetching faculties:", error);
    }
  };

  const fetchBatches = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/batches");
      const data = await response.json();
      setBatches(data);
    } catch (error) {
      console.error("Error fetching batches:", error);
    }
  };

  const generateBatchName = () => {
    // Generate a unique batch name (for example, using a timestamp)
    const uniqueName = `Batch-${Date.now()}`;
    setBatchName(uniqueName);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validator.isURL(link)) {
      console.error('Invalid URL');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/api/batches', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ batchName, subject, faculty, link, date }),
      });

      if (response.ok) {
        console.log('Batch added successfully!');
        setSubject('');
        setFaculty('');
        setLink('');
        setDate('');
        fetchBatches(); // Refresh batches after adding a new one
        generateBatchName(); // Generate new batch name after adding a batch
      } else {
        console.error('Failed to add batch:', response.statusText);
      }
    } catch (error) {
      console.error('Error adding batch:', error.message);
    }
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Manage Schedule</CardTitle>
            </CardHeader>
            <CardBody>
              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label for="batchName">Batch Name</Label>
                  <Input
                    type="text"
                    name="batchName"
                    id="batchName"
                    value={batchName}
                    disabled
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="subject">Subject</Label>
                  <Input
                    type="select"
                    name="subject"
                    id="subject"
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)}
                  >
                    <option value="">Select Subject</option>
                    {subjects.map((subject) => (
                      <option key={subject._id} value={subject.subject}>
                        {subject.subject}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
                <FormGroup>
                  <Label for="faculty">Faculty</Label>
                  <Input
                    type="select"
                    name="faculty"
                    id="faculty"
                    value={faculty}
                    onChange={(e) => setFaculty(e.target.value)}
                  >
                    <option value="">Select Faculty</option>
                    {faculties.map((fac) => (
                      <option key={fac._id} value={fac.username}>
                        {fac.username}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
                <FormGroup>
                  <Label for="link">Link</Label>
                  <Input
                    type="text"
                    name="link"
                    id="link"
                    value={link}
                    onChange={(e) => setLink(e.target.value)}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="date">Date</Label>
                  <Input
                    type="date"
                    name="date"
                    id="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                  />
                </FormGroup>
                <Button color="primary" type="submit">
                  Add Batch
                </Button>
              </Form>
              <Table>
                <thead>
                  <tr>
                    <th>Batch Name</th>
                    <th>Subject</th>
                    <th>Faculty</th>
                    <th>Link</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {batches.map((batch) => (
                    <tr key={batch._id}>
                      <td>{batch.batchName}</td>
                      <td>{batch.subject}</td>
                      <td>{batch.faculty}</td>
                      <td>{batch.link}</td>
                      <td>{batch.date}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Tables;
