import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardBody, CardTitle, Row, Col, Table, Button, Form, FormGroup, Label, Input } from 'reactstrap';

const TableComponent = ({ courses, deleteCourse, editCourse }) => {
  return (
    <div>
      <Table>
        <thead>
          <tr>
            <th>Course Name</th>
            <th>Price</th>
            <th>Duration</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course, index) => (
            <tr key={index}>
              <td>{course.name}</td>
              <td>{course.price}</td>
              <td>{course.duration}</td>
              <td>
                <Button color="danger" onClick={() => deleteCourse(course.id)}>
                  Delete
                </Button>
                {' '}
                <Button color="warning" onClick={() => editCourse(course.id)}>
                  Edit
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

const ManageCoursePage = () => {
  const [courses, setCourses] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [newCourse, setNewCourse] = useState({
    name: '',
    price: '',
    duration: ''
  });

  const openForm = () => {
    setIsFormOpen(true);
  };

  const closeForm = () => {
    setIsFormOpen(false);
  };

  const addCourse = () => {
    // Assuming you have some logic to generate a unique ID for each course
    const id = courses.length + 1;
    setCourses([...courses, { ...newCourse, id }]);
    setNewCourse({ name: '', price: '', duration: '' }); // Reset form fields
    closeForm();
  };

  const deleteCourse = (id) => {
    setCourses(courses.filter(course => course.id !== id));
  };

  const editCourse = (id) => {
    // Implement edit functionality as needed
    console.log('Editing course with ID:', id);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCourse((prevCourse) => ({
      ...prevCourse,
      [name]: value,
    }));
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h5">Manage Courses</CardTitle>
              <p className="card-category">Add and manage courses</p>
            </CardHeader>
            <CardBody>
              <Button color="primary" onClick={openForm}>Add Course</Button>
              {isFormOpen && (
                <Form>
                  <FormGroup>
                    <Label for="courseName">Course Name:</Label>
                    <Input
                      type="text"
                      id="courseName"
                      name="name"
                      value={newCourse.name}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="price">Price:</Label>
                    <Input
                      type="number"
                      id="price"
                      name="price"
                      value={newCourse.price}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="duration">Duration:</Label>
                    <Input
                      type="text"
                      id="duration"
                      name="duration"
                      value={newCourse.duration}
                      onChange={handleInputChange}
                      required
                    />
                  </FormGroup>
                  <Button color="success" onClick={addCourse}>Add</Button>
                </Form>
              )}

              <TableComponent
                courses={courses}
                deleteCourse={deleteCourse}
                editCourse={editCourse}
              />
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default ManageCoursePage;
